type CipherDetails struct {
	Name        string
	Protocol    string
	Security    string
	KeyExchange string
	Auth        string
	Encryption  string
	Hash        string
}

func TlsTest(host, port string) {
	if port == "" {
		port = "443"
	}

	addr := net.JoinHostPort(host, port)

	// pretty URL exactly as you want
	URL := host
	if port == "443" && !strings.HasPrefix(host, "https://") {
		URL = "https://" + host
	} else if port != "443" {
		URL = net.JoinHostPort(host, port)
	}

	fmt.Printf("Testing %s  \n\n", URL)

	for id, details := range cipherSuites {
		config := &tls.Config{
			ServerName:         host,
			InsecureSkipVerify: true,
			MinVersion:         tls.VersionTLS10,
			CipherSuites:       []uint16{id}, // force only this suite
		}

		conn, err := net.Dial("tcp", addr)
		if err != nil {
			continue // can't connect  skip
		}

		tlsConn := tls.Client(conn, config)
		if err := tlsConn.Handshake(); err != nil {
			conn.Close()
			continue // server rejected this suite
		}

		// success  print details
		fmt.Printf("TLS cipher detected:  0x%04X  %s\n", id, details.Name)
		fmt.Printf("- TLS version : %s\n", tls.VersionName(tlsConn.ConnectionState().Version))
		
		// colourise security level
		sec := details.Security
		var coloured string
		switch sec {
		case "Recommended":
			coloured = "\033[32m" + sec + "\033[0m"        // dark green
		case "Secure":
			coloured = "\033[92m" + sec + "\033[0m"        // light green
		case "Weak":
			coloured = "\033[33m" + sec + "\033[0m"        // yellow
		case "Insecure":
			coloured = "\033[31m" + sec + "\033[0m"        // red
		default:
			coloured = sec
		}
		fmt.Printf("- Security    : %s\n", coloured)
		fmt.Printf("- Key Exchange: %s\n", details.KeyExchange)
		fmt.Printf("- Auth        : %s\n", details.Auth)
		fmt.Printf("- Encryption  : %s\n", details.Encryption)
		fmt.Printf("- Hash        : %s\n\n", details.Hash)

		tlsConn.Close()
	}
}

